img_dir = curdir + '/imgs/'
files = listdir(img_dir)

totalCardsfact = 0
totalCards = 0
correctRecognized = 0

for img_name in files:
    img = Image.open(img_dir + img_name)
    print(img_name, end='\t')

    correctCards = cd[img_name].split()
    totalCardsfact += len(correctCards)
    for i in range(5):
        crop = img.crop((143 + i * 72, 586, 206 + i * 72, 673))
        bnw = crop.convert('L')
        imgData = np.asarray(bnw)
        thresholdedData = (imgData > 118) * 1
        price = thresholdedData[5:29, 6:30]
        suit = thresholdedData[55:75, 22:42]
        total = sum(sum(thresholdedData))
        # filter empty
        if total < 11:
            continue

        priceChance = []
        for j in p:
            priceChance.append(np.corrcoef(cardPriceNSuit[j].ravel(), price.ravel())[0][1])
        chancep = max(priceChance)
        guesp = p[priceChance.index(chancep)]
        suitChance = []
        for j in s:
            suitChance.append(np.corrcoef(cardPriceNSuit[j].ravel(), suit.ravel())[0][1])
        chances = max(suitChance)
        guess = s[suitChance.index(chances)]
        print(guesp, guess, sep='', end=' ')
        totalCards += 1
        if guesp+guess in correctCards:
            correctRecognized += 1
        else:
            print('errr, should be:', correctCards, end=' ')
    print()
print('total fact:', totalCardsfact, 'total seen:', totalCards, 'correct', correctRecognized, 'correct', correctRecognized/totalCardsfact*100, '%')
